import React, { useContext, useState } from 'react'
import Upload from '../img/upload-image-2.webp'
import Attach from '../img/attach.png'
import { arrayUnion, doc, Timestamp, updateDoc } from 'firebase/firestore';
import { db, storage } from '../Pages/Firebase';
import { v4 as uuid } from 'uuid';
import { AuthContext } from '../context/authContext';
import { ChatContext } from '../context/chatContext';
import { ref } from 'firebase/storage';

function Input() {
  const [text,setText]=useState("");
  const [img,setImg]=useState(null)

  const {currentUser} =useContext(AuthContext)
  const {data}=useContext(ChatContext);


  const handleSend= async ()=>{
    if(img){
      const storageRef = ref(storage,uuid);

      await uploadBytesResumable(storageRef, img)


    }else{
      await updateDoc(doc(db,'chats',data.chatID),{
        messages:arrayUnion({
          id:uuid,
          text,
          senderID:currentUser.uid,
          Date:Timestamp.now()
        })
      })
    }
  }

  return (
    <div className='input'>
      <input type='text' placeholder='Type Something Here....'  onChange={(e)=>setText(e.targer.value)}/>
      <div className='send'>
          <img src={Attach} alt="attach" />
          <input type="file" style={{display:'none'}} id='file' onChange={(e)=>setImg(e.target.files[0])}/>
          <label htmlFor="file">
            <img src={Upload} alt="" />
          </label>
          <button>Send</button>
      </div>
    </div>
  )
}

export default Input
