import React, { useState } from 'react'
import '../style.scss'
import Avatar from '../img/avatar.png'
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth, storage, db } from '../Pages/Firebase'
import {ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { doc, setDoc } from "firebase/firestore"; 
import { useNavigate } from 'react-router-dom';
// eslint-disable-next-line
function Register() {
  const [err,setErr]= useState(false);
  const navigate= useNavigate();
  const handleSubmit= async (e)=>{
      e.preventDefault();
      const displayName = e.target[0].value;
      const email = e.target[1].value;
      const password = e.target[2].value;
      const file = e.target[3].files[0];
      try{
      const resposne= await createUserWithEmailAndPassword(auth, email, password);
/**********storage */
      const storageRef = ref(storage, displayName);
      const uploadTask = uploadBytesResumable(storageRef, file);
      uploadTask.on(
      (error) => {
          setErr(true)
      }, 
  () => {
    getDownloadURL(uploadTask.snapshot.ref).then( async (downloadURL) => {
      await updateProfile(resposne.user,{
        displayName,
        photoURL:downloadURL
      });
      await setDoc(doc(db, "users", resposne.user.uid), {
        uid:resposne.user.uid,
        displayName,
        email,
        photoURL:downloadURL
      });
/***chat */
      await setDoc(doc(db, "userChat", resposne.user.uid), {});
      navigate('/');
/***chat */
    });
  }
);
/**********storage */
      }
      catch(err){
        setErr(true)
      }


  }
  return (
    <div className='formContainer'>
      <div className='formWrapper'>
        <span className='title'>Register Here</span>
        <form  onSubmit={handleSubmit} >
          <input type="text" placeholder='Enter Your Name'/>
          <input type="email" placeholder='Enter Email'/>
          <input type="password" placeholder='Password'/>
          <input style={{display:"none"}} type="file" id='file'/>
          <label htmlFor='file'><img src={Avatar} alt=""/>
          <span>Add an avatar</span></label>
          <button>Sign Up</button>
          {err && <span>Something Went Wrong</span>}
        </form>
        <p>If you already have an account! Login</p>
      </div>
    </div>
  )
}

export default Register
