import React from 'react'
import '../style.scss'
import Avatar from '../img/avatar.png'
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from '../Pages/Firebase'
// eslint-disable-next-line
function Register() {
  const [err,setErr]=("");
  const handleSubmit= async (e)=>{
      e.preventDefault();

      const displayName = e.target[0].value;
      const email = e.target[1].value;
      const password = e.target[2].value;
      const file = e.target[3].files[0];
      try{
      const resposne= await createUserWithEmailAndPassword(auth, email, password);
      }
      catch(err){
        setErr(err)
      }


  }
  return (
    <div className='formContainer'>
      <div className='formWrapper'>
        <span className='title'>Register Here</span>
        <form  onSubmit={handleSubmit} >
          <input type="text" placeholder='Enter Your Name'/>
          <input type="email" placeholder='Enter Email'/>
          <input type="password" placeholder='Password'/>
          <input style={{display:"none"}} type="file" id='file'/>
          <label htmlFor='file'><img src={Avatar} alt=""/>
          <span>Add an avatar</span></label>
          <button>Sign Up</button>
        </form>
        <p>If you already have an account! Login</p>
      </div>
    </div>
  )
}

export default Register
